const crypto = require('crypto');
const timestamp = Date.now().toString();
const nonce = crypto.randomBytes(16).toString('hex');
const signatureBase = context.apiKey + timestamp + nonce + request.method + request.url;
const signature = crypto.createHmac('sha256', context.apiSecret).update(signatureBase).digest('hex');
result.headers['X-API-Key'] = context.apiKey;
result.headers['X-Timestamp'] = timestamp;
result.headers['X-Nonce'] = nonce;
result.headers['X-Signature'] = signature;
